import React, { useState, useEffect } from 'react';
import {
  Typography,
  Box,
  Card,
  CardContent,
  Grid,
  Button,
  Chip,
  Avatar,
  useMediaQuery,
  useTheme,
  Container,
  Fab,
  Tooltip
} from '@mui/material';
import { motion } from 'framer-motion';
import {
  EmojiEvents,
  Whatshot,
  School,
  TrendingUp,
  PlayArrow,
  Add
} from '@mui/icons-material';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const { auth } = useAuth();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isSmallMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const xpToNext = (auth.user?.level || 1) * 100;
  const progress = (auth.user?.xp || 0) / xpToNext * 100;

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/v1/courses');
        setCourses(response.data);
      } catch (error) {
        console.error('Error fetching courses:', error);
        // Set mock data for demo
        setCourses([
          { _id: '1', title: 'Computer Science Basics', subject: 'Computer Science' },
          { _id: '2', title: 'Advanced Mathematics', subject: 'Mathematics' }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  const StatCard = ({ title, value, icon, gradient, subtitle, action, ...props }) => (
    <motion.div
      whileHover={{ scale: isMobile ? 1.02 : 1.05 }}
      whileTap={{ scale: 0.98 }}
      style={{ height: '100%' }}
    >
      <Card
        {...props}
        sx={{
          height: '100%',
          background: gradient,
          color: 'white',
          position: 'relative',
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(255, 255, 255, 0.1)',
            opacity: 0,
            transition: 'opacity 0.3s ease',
          },
          '&:hover::before': {
            opacity: 1,
          }
        }}
      >
        <CardContent sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
          <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
            <Box>
              <Typography variant={isSmallMobile ? "h5" : "h4"} component="div" gutterBottom>
                {icon} {title}
              </Typography>
              {subtitle && (
                <Typography variant="body2" sx={{ opacity: 0.9 }}>
                  {subtitle}
                </Typography>
              )}
            </Box>
            {!isMobile && (
              <Avatar sx={{ bgcolor: 'rgba(255,255,255,0.2)', width: 56, height: 56 }}>
                {icon}
              </Avatar>
            )}
          </Box>

          <Typography variant="h3" component="div" gutterBottom fontWeight="bold">
            {value}
          </Typography>

          {action && (
            <Button
              variant="contained"
              startIcon={<PlayArrow />}
              sx={{
                mt: 'auto',
                bgcolor: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                '&:hover': {
                  bgcolor: 'rgba(255,255,255,0.3)',
                }
              }}
            >
              {action}
            </Button>
          )}
        </CardContent>
      </Card>
    </motion.div>
  );

  if (loading) {
    return (
      <Container maxWidth="lg">
