import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Paper, Alert } from '@mui/material';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const { setAuth } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      setAuth({ token: res.data.token, user: res.data.user });
      navigate('/dashboard');
    } catch (error) {
      setError('Login failed: Invalid credentials');
    }
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
      <Paper elevation={3} sx={{ p: 4, width: 400 }}>
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Typography variant="h4" textAlign="center" gutterBottom sx={{ color: '#3f51b5' }}>
            ðŸ”‘ Login
          </Typography>
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          <form onSubmit={handleSubmit}>
            <TextField name="email" label="Email" fullWidth margin="normal" variant="outlined" onChange={handleChange} />
            <TextField name="password" label="Password" type="password" fullWidth margin="normal" variant="outlined" onChange={handleChange} />
            <Button type="submit" variant="contained" fullWidth sx={{ mt: 2, bgcolor: '#3f51b5' }}>Login</Button>
          </form>
        </motion.div>
      </Paper>
    </Box>
  );
};

export default Login;
