import React, { useState, useEffect } from 'react';
import { Typography, Box, Card, CardContent, Grid, Button, LinearProgress } from '@mui/material';
import { motion } from 'framer-motion';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const { auth } = useAuth();
  const [courses, setCourses] = useState([]);
  const xpToNext = (auth.user?.level || 1) * 100;
  const progress = (auth.user?.xp || 0) / xpToNext * 100;

  useEffect(() => {
    axios.get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <Box mt={5}>
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
        <Typography variant="h3" gutterBottom sx={{ color: '#3f51b5' }}>
          🌟 Welcome, {auth.user?.username}!
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Card sx={{ bgcolor: '#e3f2fd' }}>
              <CardContent>
                <Typography variant="h5">🏆 Level {auth.user?.level}</Typography>
                <LinearProgress variant="determinate" value={progress} sx={{ mt: 1 }} />
                <Typography>XP: {auth.user?.xp} / {xpToNext}</Typography>
                <Button variant="contained" sx={{ mt: 1 }}>View Progress</Button>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ bgcolor: '#fff3e0' }}>
              <CardContent>
                <Typography variant="h5">🔥 Streak: 0</Typography>
                <Typography>Badges: 0</Typography>
                <Typography>Achievements: 0</Typography>
                <Button variant="contained" sx={{ mt: 1 }}>View Achievements</Button>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card sx={{ bgcolor: '#e8f5e8' }}>
              <CardContent>
                <Typography variant="h5">📚 Subjects to Master</Typography>
                {courses.slice(0, 2).map(course => (
                  <Typography key={course._id}>{course.title}</Typography>
                ))}
                <Button variant="contained" sx={{ mt: 1 }}>Explore</Button>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </motion.div>
    </Box>
  );
};

export default Dashboard;
