import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { auth, setAuth } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setAuth({ token: null, user: null });
    navigate('/');
  };

  return (
    <AppBar
      position="static"
      sx={{
        bgcolor: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
      }}
    >
      <Toolbar>
        <Typography
          variant="h4"
          component={Link}
          to="/"
          sx={{
            textDecoration: 'none',
            color: 'white',
            flexGrow: 1,
            fontWeight: 'bold',
            textShadow: '1px 1px 2px rgba(0,0,0,0.3)',
          }}
        >
          ðŸŽ® Gamified Learning
        </Typography>
        <Box>
          {auth.token ? (
            <>
              <Button
                color="inherit"
                component={Link}
                to="/dashboard"
                sx={{
                  mx: 1,
                  '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' },
                  fontSize: '1.1rem',
                }}
              >
                Dashboard
              </Button>
              <Button
                color="inherit"
                component={Link}
                to="/admin"
                sx={{
                  mx: 1,
                  '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' },
                  fontSize: '1.1rem',
                }}
              >
                Admin
              </Button>
              <Button
                color="inherit"
                onClick={handleLogout}
                sx={{
                  mx: 1,
                  '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' },
                  fontSize: '1.1rem',
                }}
              >
                Logout
              </Button>
            </>
          ) : (
            <>
              <Button
                color="inherit"
                component={Link}
                to="/login"
                sx={{
                  mx: 1,
                  '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' },
                  fontSize: '1.1rem',
                }}
              >
                Login
              </Button>
              <Button
                color="inherit"
                component={Link}
                to="/signup"
                sx={{
                  mx: 1,
                  '&:hover': { bgcolor: 'rgba(255,255,255,0.1)' },
                  fontSize: '1.1rem',
                }}
              >
                Signup
              </Button>
            </>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
